// ✅ 推荐：使用 interface 定义对象类型
interface User {
  id: number
  name: string
  email: string
  avatar?: string
}

// ✅ 推荐：使用 type 定义联合类型
type Status = 'pending' | 'success' | 'error'

// ✅ 推荐：使用泛型
interface ApiResponse<T> {
  data: T
  message: string
  status: number
}

// ✅ 推荐：明确的参数和返回类型
const fetchUser = async (id: number): Promise<User> => {
  const response = await $fetch<ApiResponse<User>>(`/api/users/${id}`)
  return response.data
}

// ✅ 推荐：使用可选参数
const createUser = (data: Partial<User>): Promise<User> => {
  return $fetch('/api/users', {
    method: 'POST',
    body: data
  })
}

// composables/useAuth.ts
export const useAuth = () => {
  const user = ref<User | null>(null)
  const isLoggedIn = computed(() => !!user.value)

  const login = async (credentials: LoginCredentials) => {
    const response = await $fetch('/api/auth/login', {
      method: 'POST',
      body: credentials
    })
    user.value = response.user
  }

  const logout = async () => {
    await $fetch('/api/auth/logout', { method: 'POST' })
    user.value = null
  }

  return {
    user: readonly(user),
    isLoggedIn,
    login,
    logout
  }
}

// composables/useGlobalState.ts
const globalState = reactive({
  theme: 'light',
  sidebar: {
    isOpen: false,
    width: 280
  }
})

export const useGlobalState = () => {
  return {
    state: readonly(globalState),
    updateTheme: (theme: string) => {
      globalState.theme = theme
    },
    toggleSidebar: () => {
      globalState.sidebar.isOpen = !globalState.sidebar.isOpen
    }
  }
}

// server/api/users/index.get.ts
export default defineEventHandler(async (event) => {
  const query = getQuery(event)

  // 验证查询参数
  const { page = 1, limit = 10 } = query

  try {
    const users = await getUsersFromDatabase({
      page: Number(page),
      limit: Number(limit)
    })

    return {
      data: users,
      pagination: {
        page: Number(page),
        limit: Number(limit),
        total: users.length
      }
    }
  } catch (error) {
    throw createError({
      statusCode: 500,
      statusMessage: 'Failed to fetch users'
    })
  }
})

// composables/useUsers.ts
export const useUsers = () => {
  const users = ref<User[]>([])
  const loading = ref(false)
  const error = ref<string | null>(null)

  const fetchUsers = async (params?: { page?: number; limit?: number }) => {
    loading.value = true
    error.value = null

    try {
      const response = await $fetch('/api/users', {
        query: params
      })
      users.value = response.data
    } catch (err) {
      error.value = 'Failed to fetch users'
      console.error(err)
    } finally {
      loading.value = false
    }
  }

  return {
    users: readonly(users),
    loading: readonly(loading),
    error: readonly(error),
    fetchUsers
  }
}

// plugins/error-handler.client.ts
export default defineNuxtPlugin(() => {
  // 处理未捕获的错误
  window.addEventListener('unhandledrejection', (event) => {
    console.error('Unhandled promise rejection:', event.reason)

    // 显示用户友好的错误消息
    const toast = useToast()
    toast.add({
      title: '操作失败',
      description: '请稍后重试或联系支持团队',
      color: 'red'
    })
  })
})

// composables/useCache.ts
export const useCache = <T>(key: string, fetcher: () => Promise<T>) => {
  return useLazyAsyncData(key, fetcher, {
    // 缓存 5 分钟
    maxAge: 1000 * 60 * 5,
    // 在客户端和服务端都缓存
    server: true,
    client: true
  })
}

// tests/components/UserCard.test.ts
import { mount } from '@vue/test-utils'
import UserCard from '~/components/UserCard.vue'

describe('UserCard', () => {
  const mockUser = {
    id: 1,
    name: 'John Doe',
    email: 'john@example.com'
  }

  it('renders user information correctly', () => {
    const wrapper = mount(UserCard, {
      props: { user: mockUser }
    })

    expect(wrapper.text()).toContain('John Doe')
    expect(wrapper.text()).toContain('john@example.com')
  })

  it('emits delete event when delete button is clicked', async () => {
    const wrapper = mount(UserCard, {
      props: { user: mockUser }
    })

    await wrapper.find('[data-testid="delete-button"]').trigger('click')

    expect(wrapper.emitted('delete')).toBeTruthy()
    expect(wrapper.emitted('delete')[0]).toEqual([mockUser.id])
  })
})

